    {text:'Llorar con la llorona', completed:false},
    {text:'Tener un gato', completed: true},];

    const [tareas, settareas] = React.useState(defaultTodos);
    const tareasCompletador = tareas.filter( filtrando => !!filtrando.completed).length;
    const todasLasTareas = tareas.length;
    const Busquedatareas = tareas.filter((filtrando) => { return filtrando.text.toLocaleLowerCase().includes(busquedaValue.toLocaleLowerCase())})

  return (
   <React.Fragment>{/*<div className="App">*/}

      <TodoTitulo Contador={tareasCompletador} total={todasLasTareas}></TodoTitulo>
